	public UserManager(UserDao userDao, UserEmailAcceptService userEmailAccept) {
		super();
		this.userDao = userDao;
		this.userEmailAccept = userEmailAccept;
	}

	@Override
	public void add(User user) {
		if (user.getName().length() < 2 || user.getSurname().length() < 2) {
			System.out.println("Name and Surname must be at least 2 characters, register failed.");
			return;
		} else if (user.getPassword().length() < 6) {
			System.out.println("Password must be at least 6 characters, register failed.");
			return;
		} else if (!ptr.matcher(user.getEmail()).matches() || !isEmailUnique()) {
			System.out.println("The email you entered is invalid, register failed.");
			return;
		} else {
			userEmailAccept.SendVerificationEmail(user);
			userEmailAccept.LinkClikked(user);
			this.userDao.add(user);
			System.out.println(user.getName() + " kayýt baþarýlý.\n");
		}
	}

	@Override
	public Boolean isEmailUnique() {
		// TODO Auto-generated method stub
		return true;
	}

